<div id="comments">            <?php $this->comments()->to($comments); ?>            <?php if ($comments->have()): ?>			<p><?php $this->commentsNum(_t('还有沙发可以抢\(^o^)/'), _t('沙发没有了，板凳也要抢'), _t('已有 %d 条评论')); ?> &raquo;</p>                    <?php $comments->pageNav(); ?>                       <?php $comments->listComments(); ?>                    <?php endif; ?>            <?php if($this->allow('comment')): ?>            <div id="<?php $this->respondId(); ?>" class="respond">                     <div class="cancel-comment-reply">            <?php $comments->cancelReply(); ?>            </div>            			<h4 id="response"><?php _e('添加新评论'); ?> &raquo;</h4>			<form method="post" action="<?php $this->commentUrl() ?>" id="comment_form">                <?php if($this->user->hasLogin()): ?>				<p>Logged in as <a href="<?php $this->options->profileUrl(); ?>"><?php $this->user->screenName(); ?></a>. <a href="<?php $this->options->logoutUrl(); ?>" title="Logout"><?php _e('退出'); ?> &raquo;</a></p>                <?php else: ?>				<p>					<input type="text" name="author" id="author" class="text" size="15" value="<?php $this->remember('author'); ?>" />					<label for="author">我才不会去猜你是谁<span>必须要填的哦</span></label>				</p>				<p>					<input type="text" name="mail" id="mail" class="text" size="15" value="<?php $this->remember('mail'); ?>" />					 <label for="mail">不会吧你还没有邮件<span>必须要填的哦</span><?php if ($this->options->commentsRequireMail): ?><?php endif; ?></label>				</p>				<p>					<input type="text" name="url" id="url" class="text" size="15" value="<?php $this->remember('url'); ?>" />					<label for="url">你告诉你的网址我才会去你那里瞄瞄<?php if ($this->options->commentsRequireURL): ?><?php endif; ?></label>				</p>                <?php endif; ?>				<p><textarea rows="5" cols="50" name="text" class="textarea" onkeydown="if(event.ctrlKey&&event.keyCode==13){document.getElementById('misubmit').click();return false};"><?php $this->remember('text'); ?></textarea></p>				<p><input type="submit" value="<?php _e('提交评论（可以直接使用键盘Ctrl+Enter哦）'); ?>" class="submit radius_4" id="misubmit"/></p>			</form>            </div>            <?php else: ?>            <h4><?php _e('评论已关闭'); ?></h4>            <?php endif; ?>		</div>