<?phpif (!defined('__TYPECHO_ROOT_DIR__')) exit;function themeConfig($form) {	$hiddenHead = new Typecho_Widget_Helper_Form_Element_Checkbox('hiddenHead',	array('ShowAbout' => _t('显示关于我'),    'ShowContact' => _t('显示联系我')),	array('ShowAbout', 'ShowContact'), _t('当网站名太长的时候，需要隐藏头部其他一栏(取消选中)'),_t('侧边栏在大于1200的显示器是固定高度，建议只选择2项'));		$logoWidth = new Typecho_Widget_Helper_Form_Element_Checkbox('logoWidth',	array('ChangWidth' => _t('当两栏显示时，需要把#logo的宽度设置为 550px')),	array('ChangWidth='), _t('需要两栏显示时选中'),_t(' '));		$form->addInput($hiddenHead->multiMode());	$form->addInput($logoWidth->multiMode());		$blogAnalytics = new Typecho_Widget_Helper_Form_Element_Text('blogAnalytics', NULL, NULL, _t('统计代码'), _t('输入统计代码'));    $form->addInput($blogAnalytics);}//下一篇function theNext($widget, $default = NULL){    $db = Typecho_Db::get();    $sql = $db->select()->from('table.contents')              ->where('table.contents.created > ?', $widget->created)              ->where('table.contents.status = ?', 'publish')              ->where('table.contents.type = ?', $widget->type)              ->where('table.contents.password IS NULL')              ->order('table.contents.created', Typecho_Db::SORT_ASC)              ->limit(1);    $content = $db->fetchRow($sql);     if ($content) {        $content = $widget->filter($content);        $link = '<a href="' . $content['permalink'] . '" title="下一篇:' . $content['title'] . '">→</a>';        echo $link;    } else {        echo $default;    }} //上一篇function thePrev($widget, $default = NULL){    $db = Typecho_Db::get();    $sql = $db->select()->from('table.contents')              ->where('table.contents.created < ?', $widget->created)              ->where('table.contents.status = ?', 'publish')              ->where('table.contents.type = ?', $widget->type)              ->where('table.contents.password IS NULL')              ->order('table.contents.created', Typecho_Db::SORT_DESC)              ->limit(1);    $content = $db->fetchRow($sql);     if ($content) {        $content = $widget->filter($content);        $link = '<a href="' . $content['permalink'] . '" title="上一篇:' . $content['title'] . '">←</a>';        echo $link;    } else {        echo $default;    }}