<?php/** * 蓝色调模板，有透明效果 *  * @package Bluwater * @author 破相 * @version 0.0.0 * @link http://xfuny.com */  $this->need('header.php'); ?> <?php $this->need('banner.php'); ?> <!-- 这是调用幻灯的代码，不需要幻灯功能的可以删除本行代码 --><?php if($this->user->hasLogin()): //判断是否登录	?><!--这是前台发布功能-->			<div class="post"> <!--按个人CSS的更改-->			<?php $h2 = array('随便说说','随时随地分享身边的新鲜事~','来，说说你在想什么，做什么','嘀咕一下'); //随机显示蛋疼的引导语?><h2><?php echo $h2[(array_rand($h2))]; ?></h2>     <form action="<?php $this->options->siteUrl(); ?>action/contents-post-edit" method="post" name="write_post">         <input type="text" id="title" name="title" class="text" value="<?php echo date("Y-m-d H:i:s");?>" /><!--以发布时间作标题，把这里的hidden改成text就能自定义标题了-->         <p><textarea name="text" cols="100" rows="4" id="text" class="textarea" autocomplete="off" onkeydown='countChar("text","counter");' onkeyup='countChar("text","counter");'></textarea></p><!--输入框-->           <input type="hidden" id="allowComment" name="allowComment" value="1" checked="true" /><!--允许评论-->           <input type="hidden" name="do" value="publish" /><!--公开，可以无视-->                    <input type="submit" class="submit" value="Send" />       <p><input type="radio" name="format" id="format-post" value="post" checked="checked"><label for="format-post">微博</label><input type="radio" name="format" id="format-gallery" value="gallery"><label for="format-gallery">图片</label><input type="radio" name="format" id="format-video" value="video"><label for="format-video">视频</label><input type="radio" name="format" id="format-audio" value="audio"><label for="format-audio">音乐</label></p>     </form>								</div><!--前台发布功能end--><?php endif; ?> <!--post begin--><?php while($this->next()): ?><div class="post"><h2><span><?php $this->tags(' ', true, ' '); ?></span><a href="<?php $this->permalink() ?>"><?php $this->title() ?></a></h2><p class="entry_data"><span><?php _e('作者：'); ?><?php $this->author(); ?></span><span><?php _e('发布时间：'); ?><?php $this->date('F j, Y'); ?></span><span><?php _e('分类：'); ?><?php $this->category(','); ?></span><a href="<?php $this->permalink() ?>#comments"><?php $this->commentsNum('抢沙发', '1 条评论', '%d 条评论'); ?></a></p><?php $this->content('阅读剩余部分...'); ?><div class="clear"></div></div><?php endwhile; ?><!--post end--><div class="container_bottom"><?php $this->pageNav('上一页', '下一页', '5', '┅'); ?></div></div><!--container end --><?php $this->need('footer.php'); ?>	